------------------------------------------------------------------------------------------ 动态的美化战斗提示 (by eP)----------------------------------------------------------------------------------------if SettingsCF.misc.combat_notification == true then	local CombatNotification = CreateFrame ("Frame")	CombatNotification:RegisterEvent("PLAYER_REGEN_ENABLED")	CombatNotification:RegisterEvent("PLAYER_REGEN_DISABLED")	CombatNotification:SetScript("OnEvent", function(self, event)		if (UnitIsDeadOrGhost("player")) then return end		if event == "PLAYER_REGEN_ENABLED" then			SettingsDB.AlertRun("--COMBAT--", 0.1, 1, 0.1)		else			SettingsDB.AlertRun("++COMBAT++", 1, 0.1, 0.1)		end	end)end------------------------------------------------------------------------------------------ 动态美化的生命值/魔法值过低提示----------------------------------------------------------------------------------------if SettingsCF.misc.low_notification == true then	local LowNotification = CreateFrame ("Frame")	LowNotification:RegisterEvent("UNIT_HEALTH")	LowNotification:RegisterEvent("UNIT_MANA")	LowNotification:SetScript("OnEvent", function(self, event)		if (UnitIsDeadOrGhost("player")) then return end		if event == "UNIT_HEALTH" then			if (UnitHealth("player") / UnitHealthMax("player") <= 0.2) then				SettingsDB.AlertRun("LOW HEALTH", 1, 0.1, 0.1)				PlaySoundFile("Interface\\AddOns\\meatUI\\media\\sounds\\lowhealth.mp3")			end		end		if event == "UNIT_MANA" then			local _, powerToken = UnitPowerType("player")			if (powerToken == "MANA" and (UnitPower("player") / UnitPowerMax("player")) <= 0.2) then				SettingsDB.AlertRun("LOW MANA", 0.31, 0.45, 0.63)				PlaySoundFile("Interface\\AddOns\\meatUI\\media\\sounds\\lowmana.mp3")			end		end	end)end